package com.company;import org.w3c.dom.Document;import org.xml.sax.SAXException;import javax.xml.namespace.NamespaceContext;import javax.xml.parsers.DocumentBuilder;import javax.xml.parsers.DocumentBuilderFactory;import javax.xml.parsers.ParserConfigurationException;import javax.xml.xpath.XPath;import javax.xml.xpath.XPathExpressionException;import javax.xml.xpath.XPathFactory;import java.io.File;import java.io.IOException;import java.util.Iterator;class XMLReader {    public static void main(String[] args) throws ParserConfigurationException, IOException, SAXException, XPathExpressionException {        XMLReaderService xrs = new XMLReaderService();        DocumentBuilderFactory documentBuilderFactory = DocumentBuilderFactory.newInstance();        documentBuilderFactory.setNamespaceAware(true);        DocumentBuilder documentBuilder = documentBuilderFactory.newDocumentBuilder();        Document document = documentBuilder.parse(new File("src\\resource\\xml2.xml"));        NamespaceContext namespaceContext = new NamespaceContext() {            @Override            public String getNamespaceURI(String prefix) {                if (prefix.equals("cr")) {                    return "http://www.example.com/";                } else if (prefix.equals("xs")) {                    return "http://www.example.com/1";                } else                    return null;            }            @Override            public Iterator getPrefixes(String val) {                return null;            }            @Override            public String getPrefix(String uri) {                return null;            }        };        XPathFactory xPathFactory = XPathFactory.newInstance();        XPath xPath = xPathFactory.newXPath();        xPath.setNamespaceContext(namespaceContext);        xrs.printResultXPath(xPath, document, "//cr:shop/cr:books/cr:book");        xrs.printResultXPath(xPath, document, "//cr:shop/cr:books/xs:book");    }}