package com.company;import org.w3c.dom.Document;import org.w3c.dom.NodeList;import javax.xml.xpath.XPath;import javax.xml.xpath.XPathConstants;import javax.xml.xpath.XPathExpression;import javax.xml.xpath.XPathExpressionException;public class XMLReaderService {    public void printResultXPath(XPath xPath, Document document, String expression) throws XPathExpressionException {        XPathExpression expr = xPath.compile(expression);        Object result = expr.evaluate(document, XPathConstants.NODESET);        NodeList nodes = (NodeList) result;        for (int i = 0; i < nodes.getLength(); i++) {            System.out.println(nodes.item(i).getTextContent());        }        System.out.println("____________________________________________________");    }}